import React, { Component } from 'react';
import Escrow from './contracts/Escrow.json';
import { getWeb3 } from './utils.js';

class App extends Component {
  state = {
    web3: undefined,
    accounts: [],
    currentAccount: undefined,
    contract: undefined,
    balance: undefined,
    contractAddress: undefined
  }

  async componentDidMount() {
    const web3 = await getWeb3();
    const networkId = await web3.eth.net.getId();
    const deployedNetwork = Escrow.networks[networkId];
    const contract = new web3.eth.Contract(
      Escrow.abi,
      deployedNetwork && deployedNetwork.address,
    );

    const accounts = await web3.eth.getAccounts();
    const contractAddress = await contract._address;

    this.setState({ contractAddress });
    this.setState({ web3, accounts, contract }, this.updateBalance);
  };

  async updateBalance() {
    const { contract } = this.state;
    const balance = await contract.methods.balanceOf().call();

    this.setState({ balance });
  };

  async deposit(e) {
    e.preventDefault();
    const { contract, accounts } = this.state;
    await contract.methods.deposit().send({
      from: accounts[0],
      value: e.target.elements[0].value
    });
    this.updateBalance();
  }

  async release() {
    const { contract, accounts } = this.state;
    await contract.methods.release().send({
      from: accounts[0],
    });
    this.updateBalance();
  }

  render() {
    const { balance } = this.state;
    const { contractAddress } = this.state;
    const { accounts } = this.state;

    if (!this.state.web3) {
      return <div>Loading... Please connect your Metamask on RINKEBY Test Network!</div>;
    }

    if (!this.state.web3 && this.state.balance === undefined) {
      return <div>balance undefined</div>;
    }

    if (!this.state.web3 && this.state.balance === undefined) {
      return <div>balance undefined</div>;
    }

    if (!this.state.web3 && this.state.balance === undefined) {
      return <div>balance undefined</div>;
    }

    return (
      <div className="container">
        <h1 className="text-center">Escrow Dapp</h1>

        <div className="row">
          <div className="col-sm-12">
            <p>Contract address: <b>{contractAddress}</b></p>
          </div>
        </div>

        <div className="row">
          <div className="col-sm-12">
            <p>User wallet address: <b>{accounts}</b></p>
          </div>
        </div>

        <div className="row">
          <div className="col-sm-12">
            <p>Deposited balance: <b>{balance}</b> wei </p>
          </div>
        </div>

        <div className="row">
          <div className="col-sm-12">
            <form onSubmit={e => this.deposit(e)}>
              <div className="form-group">
                <label htmlFor="deposit">Deposit</label>
                <input type="number" className="form-control" id="deposit" />
              </div>
              <button type="submit" className="btn btn-primary">Submit</button>
            </form>
          </div>
        </div>

        <br />

        <div className="row">
          <div className="col-sm-12">
            <button onClick={() => this.release()} type="submit" className="btn btn-primary">Release</button>
          </div>
        </div>

      </div>
    );
  }
}

export default App;
